package day1;
/*
 * this program demonstrate all primitive data types in java
 */

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean bo= false;
		char c= 'c';
		byte b=100; //-128 to 127 or 0 to 256
		short s=32767;
		int i=32768;
		long l=123456789012345L;
		float f=23.45f;
		double d=54e34;
		String str = "Rakesh";
		System.out.println("Boolean values is: " +bo);
		System.out.println("Char values is : "+c);
		System.out.println("Byte values is: "+b);
		System.out.println("Short value is: "+s);
		System.out.println("Int vaue is: " +i);
		System.out.println("Long values is:"+l);
		System.out.println("float vaue is: "+f);
		System.out.println("double value is: "+d);
		System.out.println("String value is: "+str);

	}

}
